    /**
     * @covers FireflyIII\Http\Controllers\AttachmentController::update
     */
    public function testUpdate()
    {
        $this->session(['attachments.edit.url' => 'http://localhost']);

        $args = [
            'title'       => 'New title',
            'description' => 'New descr',
            'notes'       => 'New notes',
